// src/components/NetworkScanner.js
import React, { useState } from 'react';
import './NetworkScanner.css';

function NetworkScanner() {
  const [scanRange, setScanRange] = useState('');
  const [results, setResults] = useState([]);

  const handleScan = (e) => {
    e.preventDefault();
    // Implement actual scanning logic here (e.g., API call)
    // For demonstration, we'll use mock data
    const mockResults = [
      { timestamp: '2024-04-27 10:00:00', event: 'Scan Initiated', details: `Scan started for ${scanRange}` },
      { timestamp: '2024-04-27 10:05:00', event: 'Scan Completed', details: '150 active hosts found.' },
      // Add more mock results as needed
    ];
    setResults(mockResults);
  };

  return (
    <div className="scanner-container">
      <div className="scanner-card">
        <h2>Network Scanner</h2>
        <form onSubmit={handleScan}>
          <div className="form-group">
            <label htmlFor="scanRange">Scan Range</label>
            <input
              id="scanRange"
              type="text"
              placeholder="e.g., 192.168.1.0/24"
              value={scanRange}
              onChange={(e) => setScanRange(e.target.value)}
              required
            />
          </div>
          <button type="submit">Start Scan</button>
        </form>

        {results.length > 0 && (
          <div className="scanner-results">
            <h3>Scan Results</h3>
            <table>
              <thead>
                <tr>
                  <th>Timestamp</th>
                  <th>Event</th>
                  <th>Details</th>
                </tr>
              </thead>
              <tbody>
                {results.map((result, idx) => (
                  <tr key={idx}>
                    <td>{result.timestamp}</td>
                    <td>{result.event}</td>
                    <td>{result.details}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default NetworkScanner;
