// src/components/AttackDetectorPage.js
import React, { useState, useEffect } from 'react';
import './AttackDetectorPage.css';

function AttackDetectorPage() {
  const [logs, setLogs] = useState([]);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    // Simulate fetching live logs
    const interval = setInterval(() => {
      setLogs(prev => [...prev, `Log entry at ${new Date().toLocaleTimeString()}`]);
    }, 2000);

    // Simulate chart data (anomalies, bandwidth, latency)
    setChartData({
      anomalies: Math.floor(Math.random() * 10),
      bandwidthUsage: Math.floor(Math.random() * 100),
      latency: Math.floor(Math.random() * 50),
    });

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="attack-detector-page-container">
      <h2>24/7 Attack Detector Dashboard</h2>
      <p>Monitoring live logs, anomalies, and network health indicators in real-time.</p>

      <div className="charts-section">
        <div className="chart-card anomalies">
          <h3>Anomalies Detected</h3>
          <p>{chartData ? chartData.anomalies : 'Loading...'}</p>
        </div>
        <div className="chart-card bandwidth">
          <h3>Bandwidth Usage</h3>
          <p>{chartData ? `${chartData.bandwidthUsage} Mbps` : 'Loading...'}</p>
        </div>
        <div className="chart-card latency">
          <h3>Latency</h3>
          <p>{chartData ? `${chartData.latency} ms` : 'Loading...'}</p>
        </div>
      </div>

      <div className="sparkline-card">
        <h3>Recent Anomaly Trend</h3>
        <svg width="200" height="50">
          <polyline 
            fill="none" 
            stroke="#ff4d4f" 
            strokeWidth="2"
            points="0,40 20,30 40,35 60,25 80,32 100,28 120,15 140,20 160,10 180,18 200,12"
          />
        </svg>
        <p className="sparkline-label">Last few minutes</p>
      </div>

      <div className="logs-card">
        <h3>Live Logs</h3>
        <div className="logs-container">
          {logs.map((log, index) => (
            <div key={index} className="log-entry">{log}</div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default AttackDetectorPage;
