// src/components/NetworkLogging.js
import React, { useState } from 'react';
import './NetworkLogging.css';

function NetworkLogging() {
  const [searchTerm, setSearchTerm] = useState('');
  const [logs, setLogs] = useState([
    { timestamp: '2024-04-27 10:00:00', event: 'Scan Initiated', details: 'Scan started for 192.168.1.0/24' },
    { timestamp: '2024-04-27 10:05:00', event: 'Scan Completed', details: '150 active hosts found.' },
    { timestamp: '2024-04-27 11:00:00', event: 'Login Attempt', details: 'User john_doe logged in.' },
    { timestamp: '2024-04-27 12:30:00', event: 'File Upload', details: 'User jane_doe uploaded report.pdf.' },
    // Add more mock logs as needed
  ]);

  const handleSearch = (e) => {
    e.preventDefault();
    // Implement actual search logic here (e.g., API call)
    // For demonstration, we'll filter the existing logs
    const filteredLogs = logs.filter(
      (log) =>
        log.event.toLowerCase().includes(searchTerm.toLowerCase()) ||
        log.details.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setLogs(filteredLogs);
  };

  return (
    <div className="logging-container">
      <div className="logging-card">
        <h2>Network Logging</h2>
        <form className="search-group" onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search logs..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
        <div className="logging-results">
          <table>
            <thead>
              <tr>
                <th>Timestamp</th>
                <th>Event</th>
                <th>Details</th>
              </tr>
            </thead>
            <tbody>
              {logs.length > 0 ? (
                logs.map((log, idx) => (
                  <tr key={idx}>
                    <td>{log.timestamp}</td>
                    <td>{log.event}</td>
                    <td>{log.details}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" style={{ textAlign: 'center', padding: '20px' }}>
                    No logs found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default NetworkLogging;
