// src/components/NetworkDetectorPage.js
import React, { useState, useEffect } from 'react';
import './NetworkDetectorPage.css';

function NetworkDetectorPage() {
  const [logs, setLogs] = useState([]);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    // Simulate fetching logs every 2 seconds
    const interval = setInterval(() => {
      setLogs(prev => [...prev, `Log entry at ${new Date().toLocaleTimeString()}`]);
    }, 2000);

    // Simulate chart data
    setChartData({
      anomalies: Math.floor(Math.random() * 10),
      bandwidthUsage: Math.floor(Math.random() * 100),
      latency: Math.floor(Math.random() * 50),
    });

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="network-detector-page">
      <h2>Network Detector Dashboard</h2>
      <p>Monitoring live logs and network activity...</p>

      <div className="charts-container">
        {/* Mock charts section, just placeholders */}
        <div className="chart-box anomalies">
          <h3>Anomalies Detected</h3>
          <p>{chartData ? chartData.anomalies : 'Loading...'}</p>
        </div>
        <div className="chart-box bandwidth">
          <h3>Bandwidth Usage</h3>
          <p>{chartData ? chartData.bandwidthUsage + ' Mbps' : 'Loading...'}</p>
        </div>
        <div className="chart-box latency">
          <h3>Latency</h3>
          <p>{chartData ? chartData.latency + ' ms' : 'Loading...'}</p>
        </div>
      </div>

      <div className="logs-section">
        <h3>Live Logs</h3>
        <div className="logs-container">
          {logs.map((log, index) => (
            <div key={index} className="log-entry">{log}</div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default NetworkDetectorPage;
