// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar({ isAuthenticated, setIsAuthenticated }) {
  const [dropdown, setDropdown] = useState(false);

  const toggleDropdown = () => setDropdown(!dropdown);

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  return (
    <div className="navbar">
      <div className="navbar-content">
        <span className="navbar-title">Network Tool</span>
        <button className="menu-btn" onClick={toggleDropdown} aria-label="Open user menu">
          {/* Simple User Icon */}
          <svg className="user-icon" fill="currentColor" viewBox="0 0 20 20">
            <path
              fillRule="evenodd"
              d="M10 18a8 8 0 100-16 8 8 0 000 16zm-1-7a1 1 0 112-0 1 1 0 01-2 0zm1-9a3 3 0 100 6 3 3 0 000-6z"
              clipRule="evenodd"
            />
          </svg>
        </button>
        {dropdown && (
          <div className="dropdown-menu">
            {isAuthenticated ? (
              <>
                <Link to="/dashboard" onClick={() => setDropdown(false)}>Dashboard</Link>
                <Link to="/settings" onClick={() => setDropdown(false)}>Settings</Link>
                <Link to="/account" onClick={() => setDropdown(false)}>Account</Link>
                <button onClick={() => { handleLogout(); setDropdown(false); }} className="logout-button">Logout</button>
              </>
            ) : (
              <>
                <Link to="/login" onClick={() => setDropdown(false)}>Login</Link>
                <Link to="/signup" onClick={() => setDropdown(false)}>Signup</Link>
                <Link to="/network-scanner" onClick={() => setDropdown(false)}>Scanner</Link>
                <Link to="/network-logging" onClick={() => setDropdown(false)}>Logging</Link>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Navbar;
